import Form from 'react-bootstrap/Form';
import { useState } from 'react';
import { Button } from 'react-bootstrap';
import {API_BASE_URL, ACCESS_TOKEN_NAME} from '../constants/api';
import axios from 'axios';
import { withRouter } from "react-router-dom";

const Login = (props) => {
    // Taking care of states of input fields (NUSNET data)
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [rememberMe, setRememberMe] = useState(false)

    // Function to handle the submit event
    const handleSubmit = () => {
        // e.preventDefault();
        const payload={
            "email": email,
            "password": password,
        }
        axios.post(API_BASE_URL +'login', payload)
            .then(function (response) {
                if (response.data.code === 200){
                    setEmail = "";
                    setPassword = "";
                    localStorage.setItem(ACCESS_TOKEN_NAME,response.data.token);
                    redirectToHome();
                    props.showError(null)
                }
                else if(response.data.code === 204){
                    props.showError("Username and password do not match");
                }
                else{
                    props.showError("Username does not exists");
                }
            })
            .catch(function (error) {
                console.log(error);
            });
    }

    const redirectToHome = () => {
        props.history.push('/Inputs');
    }

    const redirectToRegister = () => {
        props.history.push('/register');
    }

    return (
        <div className='login'>
            <Form onSubmit={handleSubmit()}/>
                <Form.Group size="lg" controlId="email">
                    <Form.Label>Email</Form.Label>
                    <Form.Control
                        autoFocus
                        type="email"
                        value={email}
                        onChange={(e) => {
                            setEmail(e.target.value);
                        }}/>
                </Form.Group>
                <Form.Group size="lg" controlId="email">
                    <Form.Label>Password</Form.Label>
                    <Form.Control
                        type="password"
                        value={password}
                        onChange={(e) => {
                            setPassword(e.target.value);
                        }}/>
                </Form.Group>
                <Form.Group controlId="rmbMe" size='lg'>
                    <Form.Check type="checkbox" label="Remember Me" value={rememberMe} checked={rememberMe}
                    onChange={(e) => setRememberMe(e.currentTarget.checked)} />
                </Form.Group>
                <Button variant="link" onClick={() => redirectToRegister()}>Don't have an account?</Button>
                <Button block size="lg" type="submit">
                    Login
                </Button>
        </div>
    )
}

export default withRouter(Login);
