import Form from 'react-bootstrap/Form';
import { useState } from 'react';
import { Button } from 'react-bootstrap';
import axios from 'axios';


const Login = (props) => {
    // Taking care of states of input fields (NUSNET data)
    const [state , setState] = useState({
        email : "",
        password : "",
        successMessage: null
    })

    // const [email, setEmail] = useState('')
    // const [password, setPassword] = useState('')
    // const [rememberMe, setRememberMe] = useState(false)

    const handleChange = (e) => {
        const {id , value} = e.target
        setState(prevState => ({
            ...prevState,
            [id] : value
        }))
    }

    //Function to validate form
    const validateForm = () => {
        if (!state.email || !state.password) {
            console.log('Enter Deets')
        }
        return // Check carried out here to see if user exists in database
    }

    // Function to handle the submit event
    const handleSubmit = (e) => {
        e.preventDefault();
        const payload={
            "email":state.email,
            "password":state.password,
        }
        axios.post("http://localhost:5000" +'login', payload)
            .then(function (response) {
                if(response.data.code === 200){
                    setState(prevState => ({
                        ...prevState,
                        'successMessage' : 'Login successful. Redirecting to home page..'
                    }))
                    redirectToHome();
                    props.showError(null)
                }
                else if(response.data.code === 204){
                    props.showError("Username and password do not match");
                }
                else{
                    props.showError("Username does not exists");
                }
            })
            .catch(function (error) {
                console.log(error);
            });
    }

    const redirectToHome = () => {
        props.updateTitle('Main Page')
        props.history.push('/Inputs');
    }

    return (
        <div className='login'>
            <Form onSubmit={handleSubmit}/>
                <Form.Group size="lg" controlId="email">
                    <Form.Label>Email</Form.Label>
                    <Form.Control
                        autoFocus
                        type="email"
                        value={state.email}
                        onChange={handleChange}/>
                </Form.Group>
                <Form.Group size="lg" controlId="email">
                    <Form.Label>Password</Form.Label>
                    <Form.Control
                        type="password"
                        value={state.password}
                        onChange={handleChange}/>
                </Form.Group>
                <Form.Group controlId="rmbMe" size='lg'>
                    <Form.Check type="checkbox" label="Remember Me" value={rememberMe} checked={rememberMe}
                    onChange={(e) => setRememberMe(e.currentTarget.checked)} />
                 </Form.Group>
                <Button block size="lg" type="submit" disabled={!validateForm()}>
                    Login
                </Button>
        </div>
    )
}

export default Login;
